import {
  button,
  component,
  h4,
  header,
  main,
  Router,
  section,
  useNavigate,
  useOutlet,
  usePathParams,
} from '@rvjs/core'

const App = () => {
  return main({
    children: [
      Header(),
      Router({
        '/product': {
          componentFn: () => ProductPage(),
          router: {
            '/:productName': {
              componentFn: () => ProductDetailPage(),
            },
          },
        },
      }),
    ],
  })
}

const Header = () => {
  const navigate = useNavigate()

  return header({
    children: [
      button({
        textContent: '/product',
        onclick: () => navigate('/product'),
      }),
      button({
        textContent: '/product/apple',
        onclick: () => navigate('/product/apple'),
      }),
      button({
        textContent: '/product/banana',
        onclick: () => navigate('/product/banana'),
      }),
    ],
  })
}

const ProductPage = component(() => {
  const outlet = useOutlet()

  return section({
    children: [
      h4({
        textContent: 'Product',
      }),
      outlet,
    ],
  })
})

const ProductDetailPage = component(() => {
  const { productName } = usePathParams()!

  return h4({
    textContent: `productName : ${productName}`,
  })
})

export default App
