import {
  button,
  component,
  div,
  h1,
  onDestroy,
  onMount,
  Toggle,
  useState,
} from '@rvjs/core'

const App = component(() => {
  const [show, setShow] = useState(true)

  return div({
    children: [
      button({
        textContent: 'Toggle',
        onclick: () => setShow(!show()),
      }),
      div({
        children: [
          Toggle(show, () => {
            return Content()
          }),
        ],
      }),
    ],
  })
})

const Content = component(() => {
  onMount(() => {
    console.log('Content mounted')
  })

  onDestroy(() => {
    console.log('Content destroyed')
  })

  return h1({
    textContent: 'Hello World!',
  })
})

export default App
