import {
  button,
  component,
  div,
  header,
  main,
  p,
  Router,
  useNavigate,
  useOutlet,
} from '@rvjs/core'

const App = () => {
  return main({
    children: [
      Header(),
      Router({
        '/home': {
          componentFn: () => Home(),
          router: {
            '/main': {
              componentFn: () => Main(),
            },
          },
        },
        '/about': {
          componentFn: () => About(),
        },
      }),
    ],
  })
}

const Header = () => {
  const navigate = useNavigate()

  return header({
    children: [
      button({
        textContent: '/home',
        onclick: () => navigate('/home'),
      }),
      button({
        textContent: '/home/main',
        onclick: () => navigate('/home/main'),
      }),
      button({
        textContent: '/about',
        onclick: () => navigate('/about'),
      }),
    ],
  })
}

const Home = component(() => {
  const outlet = useOutlet()

  return div({
    children: [
      p({
        textContent: 'Home',
      }),
      outlet,
    ],
  })
})

const Main = component(() => {
  return div({
    textContent: 'Main',
  })
})

const About = component(() => {
  return div({
    textContent: 'About',
  })
})

export default App
