import { button, div, p, root, Toggle } from '@rvjs/core/dom'
import { useState } from '@rvjs/core/reactive'

const App = () => {
  const [show, setShow] = useState(false)

  return div({
    children: [
      button({
        textContent: 'Toggle',
        onclick: () => setShow(!show()),
      }),
      Toggle(show, () => {
        return Content()
      }),
    ],
  })
}

const Content = () => {
  return p({
    textContent: `Hello World!`,
  })
}

root(document.getElementById('app'), App())
