import { component, div, h4, header, prop, Router } from '@rvjs/core'
import { Link } from '@rvjs/ui'

const LinkExample = component(() => {
  return div({
    children: [
      Header(),
      Router({
        '/home': {
          componentFn: Home,
        },
        '/about': {
          componentFn: About,
        },
      }),
    ],
  })
})

const Header = () => {
  return header({
    children: [
      Link({
        text: prop(() => '/home'),
        href: prop(() => '/home'),
      }),
      Link({
        text: prop(() => '/about'),
        href: prop(() => '/about'),
      }),
    ],
  })
}

const Home = component(() => {
  return h4({
    textContent: 'Home',
  })
})

const About = component(() => {
  return h4({
    textContent: 'About',
  })
})

export default LinkExample
